import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  Alert(): void{
    window.alert("hello")
  }
}
<div id="header">
  <div id="logo"><img src="https://w7.pngwing.com/pngs/452/264/png-transparent-website-house-home-world-wide-web-computer-icons-house-black-arrow-icoin-angle-web-design-building.png"></div>
  <ul id="nav">
    <li>
      <a routerLink="/home">Home</a>
    </li>
    <li>
      <a routerLink="/about">About</a>
    </li>
    <li>
      <a routerLink="/albums">Albums</a>
    </li>
  </ul>
</div>

<router-outlet></router-outlet>
